package bsj.sec03.exam02;

public class ReunStatementExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 1;			//변수 x 선언 이후 1을 저장
		int y = 2;			//변수 y 선언 이후 2을 저장
		int result = x + y;		//변수 result 선언 이후 변수 x,y 더한 값을 저장
		
		System.out.println(result);

	}

}
